import React, {useState, useEffect} from 'react'
import { InputLabel, Select, MenuItem, Button, Grid, Typography } from '@material-ui/core';
import { useForm, FormProvider } from 'react-hook-form'
import { Link } from 'react-router-dom'

import {commerce} from '../../lib/commerce'

import FormInput from './CustomTextField'
import SelectInput from './SelectInput';

const AddressForm = ({ checkoutToken, next }) => {
    const [shippingCountries, setShippingCountries] = useState([]);
    const [shippingCountry, setShippingCountry] = useState('');
    const [shippingSubdivisions, setShippingSubdivisions] = useState([]);
    const [shippingSubdivision, setShippingSubdivision] = useState('');
    const [shippingOptions, setShippingOptions] = useState([]);
    const [shippingOption, setShippingOption] = useState('');
    const methods = useForm();

    const countries = Object.entries(shippingCountries).map(([code, name]) => ({id: code, label: name}))
    const subdivisons = Object.entries(shippingSubdivisions).map(([code, name]) => ({id: code, label: name}))
    const options = shippingOptions.map((sO) => ({ id: sO.id, label: `${sO.description} - (${sO.price.formatted_with_symbol})` }))

    const fetchShippingCountries = async (checkoutTokenId) => {
        const {countries} = await commerce.services.localeListShippingCountries(checkoutTokenId);

        setShippingCountries(countries);
        setShippingCountry(Object.keys(countries)[0]);
    }

    const fetchSubdivisons = async (countryCode) => {
        const { subdivisons } = await commerce.services.localeListShippingSubdivisions(countryCode)

        setShippingSubdivisions(subdivisons);
        setShippingSubdivision(Object.keys(subdivisons)[0]);

    }

    const fetchShippingOptions = async (checkoutTokenId, country, region = null) => {
        const options = await commerce.checkout.getShippingOptions(checkoutTokenId, { country, region });

        setShippingOptions(options);
        setShippingOption(options[0].id);
    }

    useEffect(() => {
        fetchShippingCountries(checkoutToken.id)
    }, []);

    useEffect (() => {
        if(shippingCountry) fetchSubdivisons(shippingCountry);

    }, [shippingCountry]);

    useEffect (() => {
        if(shippingSubdivision) fetchShippingOptions(checkoutToken.id, shippingCountry, shippingSubdivision);
    }, [shippingSubdivision])
    return (
        <>
            <Typography variant="h6" gutterBottom>Shipping Address</Typography>
            <FormProvider {...methods}>
                <form onSubmit={methods.handleSubmit((data) => next({ ...data, shippingCountry, shippingSubdivision, shippingOption}))}>
                    <Grid container spacing={3}>
                        <FormInput required name='firstname' label="First name"/>
                        <FormInput required name='lastname' label="Last name"/>
                        <FormInput required name='address1' label="Address"/>
                        <FormInput required name='email' label="Email "/>
                        <FormInput required name='city' label="City "/>
                        <FormInput required name='zip' label="ZIP / Postal code "/>
                        <SelectInput
             label="Shipping Country"
             value={shippingCountry}
             onChange={(e) => setShippingCountry(e.target.value)}
             options={Object.entries(
               shippingCountries
             ).map(([code, name]) => ({ id: code, label: name }))}
           />
           <SelectInput
             label="Shipping Subdivision"
             value={shippingSubdivision}
             onChange={(e) => setShippingSubdivision(e.target.value)}
             options={Object.entries(
               shippingSubdivisions
             ).map(([code, name]) => ({ id: code, label: name }))}
           />

           <SelectInput
             label="Shipping Option"
             value={shippingOption}
             onChange={(e) => setShippingOption(e.target.value)}
             options={shippingOptions.map(({ id, description, price }) => ({
               id,
               label: `${description} - (${price.formatted_with_symbol})`,
             }))}
           />

                        </Grid>
                        <br />
          <div style={{ display: 'flex', justifyContent: 'space-between' }}>
            <Button component={Link} variant="outlined" to="/cart">Back to Cart</Button>
            <Button type="submit" variant="contained" color="primary">Next</Button>
          </div>


                </form>
            </FormProvider>
        </>
    )
}

export default AddressForm;

