import React from 'react';

import { Typography, CssBaseline, Container, Grid, Box} from '@material-ui/core';

import styled from 'styled-components';

import useStyles from './styles';



const About = () => {
    const classes = useStyles();
    return (
        <Box clone pt={2} pr={1} pb={1} pl={2}>
      <Paper elevation={0}>
        <Grid container spacing={2} alignItems="center" wrap="nowrap">
          <Grid item>
            <Box bgcolor="primary.main" clone>
              <Avatar>
                <SignalWifiOffIcon />
              </Avatar>
            </Box>
          </Grid>
          <Grid item>
            <Typography>You have lost connection to the internet. This app is offline.</Typography>
          </Grid>
        </Grid>
        <Grid container justifyContent="flex-end" spacing={1}>
          <Grid item>
            <Button color="primary">Turn on wifi</Button>
          </Grid>
        </Grid>
      </Paper>
    </Box>

    )
}

const BackgroundBox = styled.div` 


    height: 150px;
    width: 150px;
    background-color: blue;
    border-radius: 80px;
    margin-bottom: 30px;
    float: left;
    margin-right: 20px;


`;



export default About;
